import React, { useState, useEffect, createContext, useContext } from 'react';
import { 
  Users, Calendar, Trophy, Settings, Plus, Search, Crown, 
  MapPin, Clock, User, Mail, Phone, Star, MessageCircle,
  Shield, Edit, LogOut, Save, X, ChevronRight, Heart,
  Filter, Bell, Menu, Home, ChevronDown
} from 'lucide-react';

// Auth Context
const AuthContext = createContext();

const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

// Mock Database (replace with Supabase later)
const mockDB = {
  users: [
    { 
      id: '1', 
      email: 'admin@picklematch.com', 
      name: 'System Admin', 
      role: 'admin',
      skillLevel: "Bring it on!",
      bio: 'System administrator',
      verified: true,
      status: 'active'
    }
  ],
  facilities: [
    {
      id: '1',
      name: 'Rogers Adult Wellness Center',
      address: '2001 W Persimmon St, Rogers AR 72756',
      description: '3 indoor wood courts - reservations & membership required',
      phone: '479-631-3333',
      email: '',
      latitude: 36.3293,
      longitude: -94.1561,
      amenities: ['Indoor Courts', 'Restrooms', 'Water', 'Lights'],
      rating: 4.2,
      verified: true,
      courts: 3,
      type: 'indoor'
    },
